// Prepare data for storage with timestamp
const statistics = $('Comodo - Get Device Statistics').first().json;
const devices = $('Comodo - Get All Devices with CCS').all();

const timestamp = new Date().toISOString();
const dateKey = timestamp.split('T')[0];

// Aggregate device metrics
const metrics = {
  date: dateKey,
  timestamp: timestamp,
  totalDevices: devices.length,
  onlineDevices: devices.filter(d => d.json.online_status === 1).length,
  offlineDevices: devices.filter(d => d.json.online_status === 2).length,

  // AV Status breakdown
  avInstalled: devices.filter(d => d.json.ccs_installed === true).length,
  avRunning: devices.filter(d => d.json.ccs_status === 'Running').length,
  avOutdated: devices.filter(d => d.json.ccs_db_outdated === true).length,

  // OS breakdown
  windowsDevices: devices.filter(d => d.json.os_type === 1).length,
  macDevices: devices.filter(d => d.json.os_type === 2).length,
  linuxDevices: devices.filter(d => d.json.os_type === 3).length,

  // Summary statistics from API
  summaryStats: statistics,

  // Detailed device data for drill-down
  deviceDetails: devices.map(d => ({
    id: d.json.id,
    name: d.json.name,
    company: d.json.company_name,
    online: d.json.online_status === 1,
    avStatus: d.json.ccs_status,
    lastSeen: d.json.was_active_at
  }))
};

return { json: metrics };
